import React from 'react';


// Mostre os dados da aplicação, como aprensetado no vídeo
// Não utilize CSS externo, use o style para mudar as cores
// Se a situação estiver ativa pinte de verde, inativa vermelho
// Se o gasto for maior que 10000 mostre uma mensagem
const luana = {
  cliente: 'Luana',
  idade: 27,
  compras: [
    { nome: 'Notebook', preco: 'R$ 2500' },
    { nome: 'Geladeira', preco: 'R$ 3000' },
    { nome: 'Smartphone', preco: 'R$ 1500' },
  ],
  ativa: true,
};

const mario = {
  cliente: 'Mario',
  idade: 31,
  compras: [
    { nome: 'Notebook', preco: 'R$ 2500' },
    { nome: 'Geladeira', preco: 'R$ 3000' },
    { nome: 'Smartphone', preco: 'R$ 1500' },
    { nome: 'Guitarra', preco: 'R$ 3500' },
  ],
  ativa: false,
};

const clientes = [luana, mario];

const aplicaEstiloSituacao = (status) => {
  let color = undefined;
  status === true ? color = 'green' : color = 'red';
  const estiloSituacao = {
    fontSize: '2rem',
    color: color,
  };
  return estiloSituacao;
}
const retornaStringSituacao = (status) => {
  let situacao = undefined;
  status === true ? situacao = 'Ativo' : situacao = 'Inativo';
  return situacao;
}

const App = () => {
  const dados = clientes;

  dados.forEach(function(cliente) {
    return (
      <>
        <p>Nome: <span>{cliente.cliente}</span></p>
        <p>Idade: <span>{cliente.idade}</span></p>
        {/* <p>
          Situação: 
          <span style={aplicaEstiloSituacao(cliente.ativa)}>
            {retornaStringSituacao(cliente.ativa)}
          </span>
        </p>
        <p>
          Total gasto: 
          <span>
            {cliente.compras}
          </span>
        </p> */}
      </>
    );
  })
}

export default App;
